      arrayNode.detachChildren();
      for (Node node : arrayFoldedChildren) {
        arrayNode.addChildToBack(node);
      }
      Node newCall = new Node(Token.CALL, left);
      left.addChildToBack(Node.newString("join"));
      left.addChildToBack(right.cloneTree());
      parent.replaceChild(n, newCall);
      break;
